import React, { useState } from 'react';
import Loading, { FullPageLoading, CardLoading, ListLoading } from '../../Components/Loading';

const LoadingDemo = () => {
  const [showFullPage, setShowFullPage] = useState(false);

  const loadingTypes = [
    { type: 'spinner', name: 'Spinner' },
    { type: 'dots', name: 'Dots' },
    { type: 'pulse', name: 'Pulse' },
    { type: 'ring', name: 'Ring' },
    { type: 'wave', name: 'Wave' }
  ];

  const colors = [
    { value: 'main1', name: 'الأخضر الرئيسي' },
    { value: 'main2', name: 'الذهبي' },
    { value: 'accent', name: 'الأحمر' },
    { value: 'dark', name: 'الداكن' }
  ];

  const sizes = [
    { value: 'sm', name: 'صغير' },
    { value: 'md', name: 'متوسط' },
    { value: 'lg', name: 'كبير' },
    { value: 'xl', name: 'كبير جداً' }
  ];

  return (
    <div className="p-6 bg-light min-h-screen">
      <h1 className="text-3xl font-bold text-main1 mb-8 text-center">عرض مكونات Loading</h1>

      {/* عرض جميع أنواع Loading */}
      <section className="mb-12">
        <h2 className="text-2xl font-bold text-main1 mb-6">أنواع Loading المختلفة</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {loadingTypes.map(({ type, name }) => (
            <div key={type} className="bg-white rounded-xl p-6 shadow-sm border border-gray-100">
              <h3 className="text-lg font-bold text-main1 mb-4 text-center">{name}</h3>
              <div className="flex justify-center">
                <Loading type={type} size="lg" color="main1" />
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* عرض الألوان المختلفة */}
      <section className="mb-12">
        <h2 className="text-2xl font-bold text-main1 mb-6">الألوان المختلفة</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {colors.map(({ value, name }) => (
            <div key={value} className="bg-white rounded-xl p-6 shadow-sm border border-gray-100">
              <h3 className="text-lg font-bold text-main1 mb-4 text-center">{name}</h3>
              <div className="flex justify-center">
                <Loading type="ring" size="lg" color={value} />
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* عرض الأحجام المختلفة */}
      <section className="mb-12">
        <h2 className="text-2xl font-bold text-main1 mb-6">الأحجام المختلفة</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {sizes.map(({ value, name }) => (
            <div key={value} className="bg-white rounded-xl p-6 shadow-sm border border-gray-100">
              <h3 className="text-lg font-bold text-main1 mb-4 text-center">{name}</h3>
              <div className="flex justify-center">
                <Loading type="spinner" size={value} color="main1" />
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* مكونات Loading الجاهزة */}
      <section className="mb-12">
        <h2 className="text-2xl font-bold text-main1 mb-6">مكونات Loading الجاهزة</h2>
        
        {/* CardLoading */}
        <div className="mb-8">
          <h3 className="text-xl font-bold text-main1 mb-4">CardLoading</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            <CardLoading />
            <CardLoading />
            <CardLoading />
          </div>
        </div>

        {/* ListLoading */}
        <div className="mb-8">
          <h3 className="text-xl font-bold text-main1 mb-4">ListLoading</h3>
          <ListLoading count={3} />
        </div>

        {/* زر لعرض FullPageLoading */}
        <div className="text-center">
          <button
            onClick={() => setShowFullPage(true)}
            className="bg-main1 text-light px-6 py-3 rounded-xl hover:bg-opacity-90 transition-colors"
          >
            عرض FullPageLoading
          </button>
        </div>
      </section>

      {/* أمثلة عملية */}
      <section className="mb-12">
        <h2 className="text-2xl font-bold text-main1 mb-6">أمثلة عملية</h2>
        
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {/* مثال في زر */}
          <div className="bg-white rounded-xl p-6 shadow-sm border border-gray-100">
            <h3 className="text-lg font-bold text-main1 mb-4">Loading في زر</h3>
            <button className="w-full bg-main1 text-light py-3 rounded-xl hover:bg-opacity-90 transition-colors flex items-center justify-center gap-2">
              <Loading type="dots" size="sm" color="light" />
              <span>جاري الحفظ...</span>
            </button>
          </div>

          {/* مثال في بطاقة */}
          <div className="bg-white rounded-xl p-6 shadow-sm border border-gray-100">
            <h3 className="text-lg font-bold text-main1 mb-4">Loading في بطاقة</h3>
            <div className="bg-grayish rounded-lg p-4">
              <div className="flex items-center justify-center py-8">
                <Loading type="pulse" size="lg" color="main2" text="جاري تحميل البيانات..." />
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* FullPageLoading */}
      {showFullPage && (
        <FullPageLoading text="جاري تحميل الصفحة..." />
      )}

      {/* زر إغلاق FullPageLoading */}
      {showFullPage && (
        <div className="fixed top-4 right-4 z-50">
          <button
            onClick={() => setShowFullPage(false)}
            className="bg-accent text-light px-4 py-2 rounded-lg hover:bg-opacity-90 transition-colors"
          >
            إغلاق
          </button>
        </div>
      )}
    </div>
  );
};

export default LoadingDemo;
