import React from 'react';

const ArabicLandingPage = () => {
  return (
    <div dir="rtl" className="font-sans bg-white min-h-screen text-gray-800">
      {/* Navbar */}
      <nav className="flex justify-between items-center p-4 shadow-md bg-white">
        {/* Site Logo Placeholder */}
        <div className="text-2xl font-bold">شعار الموقع</div>
        {/* Action Buttons */}
        <div className="flex gap-4">
          <button className="px-4 py-2 rounded-xl border border-gray-300 hover:bg-gray-100 transition">
            تسجيل الدخول
          </button>
          <button className="px-4 py-2 rounded-xl bg-blue-600 text-white hover:bg-blue-700 transition">
            تسجيل
          </button>
        </div>
      </nav>

      {/* Hero Section */}
      <main className="flex flex-col-reverse md:flex-row items-center justify-between px-6 md:px-20 py-16 gap-10 md:gap-20">
        {/* Text Content */}
        <div className="md:w-1/2 text-right">
          <h1 className="text-4xl md:text-5xl font-extrabold leading-snug mb-6">
            منصتك لتحقيق الإنتاجية بكفاءة واحترافية
          </h1>
          <p className="text-lg md:text-xl text-gray-600 leading-relaxed mb-6">
            نقدم لك الأدوات الذكية التي تساعدك على تنظيم أعمالك، إدارة مهامك، والتواصل مع فريقك بسهولة وسرعة.
          </p>
          <button className="px-6 py-3 bg-blue-600 text-white rounded-xl hover:bg-blue-700 transition">
            ابدأ الآن
          </button>
        </div>

        {/* Image Placeholder */}
        <div className="md:w-1/2">
          <img
            src="https://via.placeholder.com/500x400.png?text=صورة+توضيحية"
            alt="صورة توضيحية"
            className="rounded-2xl shadow-md w-full"
          />
        </div>
      </main>
    </div>
  );
};

export default ArabicLandingPage;
