// src/Utils/validateForm.js
export function validateRegisterForm(values) {
  const errors = {};

  if (!values.firstName.trim()) errors.firstName = 'الاسم الأول مطلوب';
  if (!values.lastName.trim()) errors.lastName = 'الكنية مطلوبة';
  if (!values.mobile.trim()) errors.mobile = 'رقم الموبايل مطلوب';
  if (values.password.length < 8) errors.password = 'كلمة السر يجب أن تكون 6 أحرف على الأقل';
  if (values.password !== values.confirmPassword) errors.confirmPassword = 'كلمة السر غير متطابقة';

  return errors;
}
