import React, { useState } from 'react'
import {  FaMobileAlt } from "react-icons/fa";
import logo from "../../Assets/images/logo.jpg";
import InputField from "../../Components/InputField";
import Button from "../../Components/Button";
import { handlePhoneInputKeyPress } from '../../Utils/validateForm';
import Alert from '../../Components/Alert';
import { sendOTP } from '../../API/Auth';
import { useNavigate } from 'react-router-dom';

const ForgetPassword = () => {
  const [phone, setPhone] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await sendOTP({ phone_number: phone, purpose: "reset_password" });
      setError("");
      setSuccess("تم إرسال رمز التحقق بنجاح! سيتم تحويلك خلال ثوانٍ...");
      setTimeout(() => {
        navigate("/verify-otp", { state: { formData: { phone_number: phone, purpose: 'reset_password' } } });
      }, 1800);
    } catch (error) {
      let msg = "فشل إرسال رمز OTP. تحقق من الرقم أو الشبكة.";
      if (error.response && error.response.data) {
        if (typeof error.response.data === "string") {
          msg = error.response.data;
        } else if (typeof error.response.data === "object") {
          msg = Object.values(error.response.data).flat().join("، ");
        }
      }
      setError(msg);
      setSuccess("");
    }
  };

  return (
    <div className="min-h-screen bg-light flex items-center justify-center  direction-rtl">
      <div className="w-full max-w-lg p-6 bg-light rounded-2xl shadow-md text-dark">
        <div  className="flex flex-col items-center mb-6">
          <img src={logo} alt="Logo" className="w-16 h-16" />
          <h3 className="text-2xl font-bold mt-2  text-main1"> هل نسيت كلمة المرور ؟ </h3>
          <p className="text-sm text-grayish-600">تحقق من صندوق الرسائل الخاص بك</p>
        </div>

<form className="space-y-4" onSubmit={handleSubmit}>
    <InputField className="p-3 mb-2" placeholder="رقم الموبايل" Icon={FaMobileAlt} type="text"
      value={phone}
      onChange={e => setPhone(e.target.value)}
      onKeyPress={e => handlePhoneInputKeyPress(e, e.target.value)}
    />
    {error && <Alert>{error}</Alert>}
    {success && <div className="bg-green-100 text-green-800 p-2 rounded text-center">{success}</div>}
    <div className="text-right mt-4 text-sm">
          <Button type="submit">ارسال </Button>
        </div>
</form>
      </div>
    </div>
  );
}

export default ForgetPassword;
