// src/Hooks/useRegisterForm.js
import { useState } from 'react';
import { validateRegisterForm } from '../Utils/validateForm';

export default function useRegisterForm(onSubmit) {
    const [values, setValues] = useState({
    first_name: '',
    father_name: '',
    last_name: '',
    phone_number: '',
    password: '',
    password_confirmation: '',
    });

    const [errors, setErrors] = useState({});

    const handleChange = (e) => {
    const { name, value } = e.target;
    setValues((prev) => ({
        ...prev,
        [name]: value,
        }));
    // تحقق فوري من صحة رقم الهاتف
    if (name === 'phone_number') {
        if (!/^09\d{0,8}$/.test(value) && value !== "") {
        setErrors((prev) => ({ ...prev, phone_number: 'رقم الموبايل يجب أن يبدأ بـ 09 وطوله 10 أرقام فقط' }));
        } else {
        setErrors((prev) => {
            const { phone_number, ...rest } = prev;
            return rest;
        });
        }
    }
    };
    

    const handleSubmit = (e) => {
    e.preventDefault();
    const validationErrors = validateRegisterForm(values);

    if (Object.keys(validationErrors).length === 0) {
        onSubmit(values);
    } else {
        setErrors(validationErrors);
    }
    };

    return {
    values,
    errors,
    handleChange,
    handleSubmit,
    };
}
