import React, { useState } from 'react';
import Button from '../../Components/Button';
import { FaEdit, FaTrash, FaSearch, FaChevronLeft, FaChevronRight } from 'react-icons/fa';
import userImg from '../../Assets/images/user.png';

const statusStyles = {
  Screening: 'bg-blue-100 text-blue-600',
  Interview: 'bg-orange-100 text-orange-600',
  'Phsycology Test': 'bg-blue-50 text-blue-500',
  Hired: 'bg-green-100 text-main1',
  Rejected: 'bg-red-100 text-accent',
};

const filters = [
  { label: 'All Candidates', value: 'all' },
  { label: 'Screening', value: 'Screening' },
  { label: 'Phsycology Test', value: 'Phsycology Test' },
  { label: 'Interview', value: 'Interview' },
  { label: 'Offering', value: 'Offering' },
  { label: 'Rejected', value: 'Rejected' },
];

const dummyUsers = [
  {
    name: 'Putri Pratiwi',
    role: 'Back End',
    status: 'Screening',
    date: '1/07/2024',
    city: 'Jakarta',
    img: userImg,
  },
  {
    name: 'Jacob Setiadi',
    role: 'Graphic Design',
    status: 'Interview',
    date: '21/06/2024',
    city: 'Surabaya',
    img: userImg,
  },
  {
    name: 'Leon Kennedy',
    role: 'Finance',
    status: 'Interview',
    date: '12/06/2024',
    city: 'Cirebon',
    img: userImg,
  },
  {
    name: 'Adi Pratama',
    role: 'Front End',
    status: 'Hired',
    date: '30/06/2024',
    city: 'Bandung',
    img: userImg,
  },
  {
    name: 'Jessica Robert',
    role: 'UX Designer',
    status: 'Hired',
    date: '30/06/2024',
    city: 'Bandung',
    img: userImg,
  },
  {
    name: 'Robert Downey',
    role: 'Marketing',
    status: 'Phsycology Test',
    date: '30/06/2024',
    city: 'Tangerang',
    img: userImg,
  },
  {
    name: 'Agung Wibowo',
    role: 'UX Researcher',
    status: 'Interview',
    date: '28/06/2024',
    city: 'Semarang',
    img: userImg,
  },
  {
    name: 'Yasmine Agustine',
    role: 'QA Engineer',
    status: 'Hired',
    date: '28/07/2024',
    city: 'Yogyakarta',
    img: userImg,
  },
  {
    name: 'Pratama Anggara',
    role: 'Full Stack',
    status: 'Rejected',
    date: '18/06/2024',
    city: 'Depok',
    img: userImg,
  },
];

const UsersList = () => {
  const [selectedFilter, setSelectedFilter] = useState('all');
  const [search, setSearch] = useState('');
  const [page, setPage] = useState(1);

  // تصفية البيانات حسب الفلتر والبحث
  const filteredUsers = dummyUsers.filter(user => {
    const matchesFilter = selectedFilter === 'all' || user.status === selectedFilter;
    const matchesSearch = user.name.toLowerCase().includes(search.toLowerCase());
    return matchesFilter && matchesSearch;
  });

  return (
    <div className="p-6 bg-light min-h-screen font-tajawal">
      {/* العنوان وعدد المستخدمين */}
      <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4 mb-6">
        <div>
          <h2 className="text-2xl font-bold text-main1">المرشحين</h2>
          <span className="text-gray-500 text-sm">إجمالي المرشحين <b className="text-main2">{dummyUsers.length}</b></span>
        </div>
        <div className="flex items-center gap-2">
          <span className="text-xs text-gray-400">ترتيب حسب:</span>
          <span className="text-sm text-main1 font-medium">آخر تحديث</span>
        </div>
      </div>

      {/* الفلاتر وشريط البحث */}
      <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4 mb-4">
        <div className="flex flex-wrap gap-2">
          {filters.map(f => (
            <button
              key={f.value}
              onClick={() => setSelectedFilter(f.value)}
              className={`px-4 py-1 rounded-full border text-sm font-medium transition-all duration-150
                ${selectedFilter === f.value ? 'bg-main1 text-light border-main1' : 'bg-grayish text-main1 border-gray-200 hover:bg-main1/10'}`}
            >
              {f.label}
            </button>
          ))}
        </div>
        <div className="relative w-full md:w-64">
          <input
            type="text"
            placeholder="بحث..."
            value={search}
            onChange={e => setSearch(e.target.value)}
            className="w-full pl-10 pr-4 py-2 rounded-xl border border-gray-200 bg-grayish text-main1 focus:outline-none focus:ring-2 focus:ring-main2 font-tajawal"
          />
          <FaSearch className="absolute left-3 top-1/2 -translate-y-1/2 text-main2" />
        </div>
      </div>

      {/* الجدول */}
      <div className="overflow-x-auto rounded-2xl shadow bg-white">
        <table className="min-w-full text-sm text-main1 font-tajawal">
          <thead>
            <tr className="bg-grayish text-main1">
              <th className="p-4 text-center"><input type="checkbox" /></th>
              <th className="p-4 text-right">المرشح</th>
              <th className="p-4 text-center">الحالة</th>
              <th className="p-4 text-center">تاريخ التقديم</th>
              <th className="p-4 text-center">المدينة</th>
              <th className="p-4 text-center">إجراء</th>
            </tr>
          </thead>
          <tbody>
            {filteredUsers.length === 0 ? (
              <tr>
                <td colSpan="6" className="text-center py-8 text-gray-400">لا يوجد مرشحين مطابقين</td>
              </tr>
            ) : (
              filteredUsers.map((user, idx) => (
                <tr key={idx} className="border-b last:border-b-0 hover:bg-grayish/60 transition">
                  <td className="p-4 text-center"><input type="checkbox" /></td>
                  <td className="p-4 flex items-center gap-3">
                    <img src={user.img} alt={user.name} className="w-10 h-10 rounded-full border border-gray-200 object-cover" />
                    <div>
                      <div className="font-bold text-main1">{user.name}</div>
                      <div className="text-xs text-gray-400">{user.role}</div>
                    </div>
                  </td>
                  <td className="p-4 text-center">
                    <span className={`px-3 py-1 rounded-full text-xs font-bold ${statusStyles[user.status] || 'bg-gray-100 text-gray-500'}`}>{user.status}</span>
                  </td>
                  <td className="p-4 text-center">{user.date}</td>
                  <td className="p-4 text-center">{user.city}</td>
                  <td className="p-4 text-center flex items-center gap-2 justify-center">
                    <button className="p-2 rounded-lg hover:bg-main2/10 text-main2 transition"><FaEdit /></button>
                    <button className="p-2 rounded-lg hover:bg-accent/10 text-accent transition"><FaTrash /></button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {/* التصفح */}
      <div className="flex items-center justify-between mt-6">
        <div className="text-sm text-gray-500">عرض {filteredUsers.length} من {dummyUsers.length} مرشح</div>
        <div className="flex items-center gap-2">
          <button onClick={() => setPage(p => Math.max(1, p - 1))} className="p-2 rounded-lg border border-gray-200 text-main1 disabled:opacity-50" disabled={page === 1}><FaChevronRight /></button>
          <span className="px-3 py-1 rounded bg-grayish text-main1 font-bold">{page}</span>
          <button onClick={() => setPage(p => p + 1)} className="p-2 rounded-lg border border-gray-200 text-main1"><FaChevronLeft /></button>
        </div>
      </div>
    </div>
  );
};

export default UsersList;
