import React, { useRef, useState } from 'react';
import { FaUser, FaCamera, FaSave } from 'react-icons/fa';
import InputField from '../../Components/InputField';
import Button from '../../Components/Button';

const UpdateProfile = () => {
    // مرجع لحقل رفع الصورة
    const fileInputRef = useRef(null);
    // حالة لتخزين الصورة المختارة مؤقتاً
    const [imagePreview, setImagePreview] = useState(require('../../Assets/images/user.png'));
    // حالات الحقول
    const [fields, setFields] = useState({
        first_name: '',
        father_name: '',
        last_name: ''
    });

    // دالة لفتح نافذة اختيار الصورة
    const handleImageClick = () => {
        fileInputRef.current.click();
    };

    // دالة عند اختيار صورة جديدة
    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setImagePreview(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    // دالة لتغيير الحقول
    const handleChange = (e) => {
        setFields({ ...fields, [e.target.name]: e.target.value });
    };

    return (
        <div className="direction-rtl bg-light rounded-2xl p-6 max-w-lg mx-auto font-tajawal">
            {/* نموذج تحديث البيانات */}
            <form className="space-y-5">
                {/* رفع صورة شخصية */}
                <div className="flex flex-col items-center mb-4">
                    <div className="relative w-24 h-24 mb-2">
                        <img
                            src={imagePreview}
                            alt="صورة المستخدم"
                            className="w-24 h-24 rounded-full object-cover border-2 border-main1 shadow"
                        />
                        <button
                            type="button"
                            onClick={handleImageClick}
                            className="absolute bottom-1 left-1 bg-main1 text-light p-2 rounded-full shadow hover:bg-main2 transition"
                            aria-label="تغيير الصورة الشخصية"
                        >
                            <FaCamera size={16} />
                        </button>
                        <input
                            type="file"
                            accept="image/*"
                            ref={fileInputRef}
                            className="hidden"
                            onChange={handleImageChange}
                        />
                    </div>
                    <span className="text-xs text-gray-500">الصورة الشخصية (اختياري، فقط من الواجهة)</span>
                </div>

                {/* حقول البيانات */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <InputField
                        name="first_name"
                        placeholder="الاسم الأول"
                        Icon={FaUser}
                        value={fields.first_name}
                        onChange={handleChange}
                    />
                    <InputField
                        name="father_name"
                        placeholder="اسم الأب"
                        Icon={FaUser}
                        value={fields.father_name}
                        onChange={handleChange}
                    />
                    <InputField
                        name="last_name"
                        placeholder="الكنية"
                        Icon={FaUser}
                        value={fields.last_name}
                        onChange={handleChange}
                    />
                </div>

                {/* زر الحفظ */}
                <div className="flex justify-center mt-6">
                    <Button type="button" Icon={FaSave} className=" px-8 py-2 text-lg font-bold">
                        حفظ التعديلات
                    </Button>
                </div>
            </form>
        </div>
    );
}

export default UpdateProfile;
