// src/Utils/validateForm.js
export function validateRegisterForm(values) {
  const errors = {};

  if (!values.firstName.trim()) errors.first_name = 'الاسم الأول مطلوب';
  if (!values.firstName.trim()) errors.father_name = 'الاسم الأب مطلوب';
  if (!values.lastName.trim()) errors.last_name = 'الكنية مطلوبة';
  if (!values.mobile.trim()) errors.phone_number = 'رقم الموبايل مطلوب';
  if (values.password.length < 8) errors.password = 'كلمة السر يجب أن تكون 8 أحرف على الأقل';
  if (values.password !== values.password_confirmation) errors.password_confirmation = 'كلمة السر غير متطابقة';

  return errors;
}
