import React from "react";
import { Navigate } from "react-router-dom";
import { useAuth } from "../Context/AuthContext";
import DashboardLayout from "../Components/DashboardLayout";

const ProtectedRoute = ({ children, allowedRoles }) => {
  const { isAuthenticated, user, isLoading } = useAuth();

  // انتظر حتى انتهاء التحميل قبل التحقق من المصادقة
  if (isLoading) {
    return (
      <div className="min-h-screen bg-light flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-main1 mx-auto mb-4"></div>
          <p className="text-main1 font-tajawal">جاري التحقق من الجلسة...</p>
        </div>
      </div>
    );
  }

  if (!isAuthenticated) {
    // إذا لم يكن مسجلاً الدخول، أعد توجيهه لصفحة تسجيل الدخول
    return <Navigate to="/login" replace />;
  }

  if (allowedRoles && (!user || !allowedRoles.includes(user.role))) {
    // إذا لم يكن يملك الدور المناسب
    return <Navigate to="/login" replace />;
  }

  // استخدام DashboardLayout للصفحات المحمية
  return <DashboardLayout>{children}</DashboardLayout>;
};

export default ProtectedRoute;
