import Cookies from 'universal-cookie';

const cookies = new Cookies();

// إعدادات الكوكيز الافتراضية
const defaultOptions = {
  path: '/',
  secure: process.env.NODE_ENV === 'production',
  sameSite: 'strict'
};

// إدارة التوكنز
export const tokenManager = {
  // تخزين access token
  setAccessToken: (token) => {
    console.log('🔑 حفظ access token...');
    cookies.set('access_token', token, defaultOptions);
    console.log('✅ تم حفظ access token بنجاح');
  },

  // تخزين refresh token
  setRefreshToken: (token) => {
    console.log('🔄 حفظ refresh token...');
    cookies.set('refresh_token', token, defaultOptions);
    console.log('✅ تم حفظ refresh token بنجاح');
  },

  // الحصول على access token
  getAccessToken: () => {
    return cookies.get('access_token');
  },

  // الحصول على refresh token
  getRefreshToken: () => {
    return cookies.get('refresh_token');
  },

  // تحديث التوكنز
  updateTokens: (access, refresh) => {
    cookies.set('access_token', access, defaultOptions);
    cookies.set('refresh_token', refresh, defaultOptions);
  },

  // حذف التوكنز
  removeTokens: () => {
    console.log('🗑️ حذف التوكنز من الكوكيز...');
    cookies.remove('access_token', { path: '/' });
    cookies.remove('refresh_token', { path: '/' });
    console.log('✅ تم حذف التوكنز بنجاح');
  }
};

// إدارة بيانات المستخدم مع تشفير Base64
export const userDataManager = {
  // تخزين بيانات المستخدم
  setUserData: (userData) => {
    const userInfo = {
      id: userData.id,
      first_name: userData.first_name,
      last_name: userData.last_name,
      is_superuser: userData.is_superuser,
      is_active: userData.is_active,
      role: userData.role
    };
    // تشفير Base64
    const encoded = btoa(JSON.stringify(userInfo));
    cookies.set('user_data', encoded, defaultOptions);
  },

  // الحصول على بيانات المستخدم
  getUserData: () => {
    const encoded = cookies.get('user_data');
    if (!encoded) return null;
    try {
      // فك التشفير Base64
      return JSON.parse(atob(encoded));
    } catch (e) {
      return null;
    }
  },

  // حذف بيانات المستخدم
  removeUserData: () => {
    console.log('🗑️ حذف بيانات المستخدم من الكوكيز...');
    cookies.remove('user_data', { path: '/' });
    console.log('✅ تم حذف بيانات المستخدم بنجاح');
  }
};

// إدارة الجلسة الكاملة
export const sessionManager = {
  // حفظ جلسة كاملة
  saveSession: (response) => {
    console.log('💾 بدء حفظ الجلسة...');
    const { access, refresh, user: userData } = response;
    
    tokenManager.setAccessToken(access);
    tokenManager.setRefreshToken(refresh);
    userDataManager.setUserData(userData);
    
    console.log('✅ تم حفظ الجلسة بنجاح');
  },

  // التحقق من وجود جلسة
  hasSession: () => {
    const accessToken = tokenManager.getAccessToken();
    const refreshToken = tokenManager.getRefreshToken();
    const userData = userDataManager.getUserData();
    const hasSession = !!(accessToken && refreshToken && userData);
    console.log('🔍 فحص الجلسة:', {
      hasAccessToken: !!accessToken,
      hasRefreshToken: !!refreshToken,
      hasUserData: !!userData,
      hasSession: hasSession
    });
    return hasSession;
  },

  // حذف الجلسة كاملة
  clearSession: () => {
    console.log('🧹 بدء حذف الجلسة...');
    tokenManager.removeTokens();
    userDataManager.removeUserData();
    console.log('✅ تم حذف الجلسة بنجاح');
  },

  // استعادة الجلسة
  restoreSession: () => {
    const accessToken = tokenManager.getAccessToken();
    const refreshToken = tokenManager.getRefreshToken();
    const userData = userDataManager.getUserData();
    
    console.log('🔄 محاولة استعادة الجلسة:', {
      hasAccessToken: !!accessToken,
      hasRefreshToken: !!refreshToken,
      hasUserData: !!userData
    });
    
    if (accessToken && refreshToken && userData) {
      console.log('✅ تم استعادة الجلسة بنجاح');
      return {
        accessToken,
        refreshToken,
        userData
      };
    }
    console.log('❌ فشل في استعادة الجلسة');
    return null;
  }
};

export default cookies;
