import { createContext, useContext, useState, useEffect } from 'react';
import { sessionManager } from '../Config/cookies';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // التحقق من وجود توكن عند تحميل التطبيق
  useEffect(() => {
    const session = sessionManager.restoreSession();
    
    if (session) {
      setUser(session.userData);
      setIsAuthenticated(true);
    }
  }, []);

  // تسجيل الدخول
  const login = (response) => {
    // حفظ الجلسة في الكوكيز
    sessionManager.saveSession(response);
    
    // استخراج بيانات المستخدم للـ Context
    const userInfo = {
      id: response.user.id,
      is_superuser: response.user.is_superuser,
      is_active: response.user.is_active,
      role: response.user.role
    };

    // تحديث حالة التطبيق
    setUser(userInfo);
    setIsAuthenticated(true);
  };

  // تسجيل الخروج
  const logout = () => {
    // حذف الجلسة من الكوكيز
    sessionManager.clearSession();
    
    // إعادة تعيين الحالة
    setUser(null);
    setIsAuthenticated(false);
  };

  // الحصول على التوكن الحالي
  const getAccessToken = () => {
    return sessionManager.restoreSession()?.accessToken;
  };

  // الحصول على refresh token
  const getRefreshToken = () => {
    return sessionManager.restoreSession()?.accessToken;
  };

  // تحديث التوكن
  const updateTokens = (access, refresh) => {
    const { tokenManager } = require('../Config/cookies');
    tokenManager.updateTokens(access, refresh);
  };

  const value = {
    user,
    isAuthenticated,
    login,
    logout,
    getAccessToken,
    getRefreshToken,
    updateTokens
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
