import React from 'react';
import useVerifyOTP from '../../Hooks/useVerifyOTP';
import Button from '../../Components/Button';
import logo from '../../Assets/images/logo.jpg';

export default function VerifyOTP() {
  const { inputs, handleChange, handlePaste } = useVerifyOTP();

  return (
    <div className="min-h-screen flex items-center justify-center bg-light  direction-rtl">
      <div className="bg-light rounded-3xl shadow-lg p-8 pt-4 w-full max-w-md flex flex-col items-center">
        {/* Header */}
        <div className="flex flex-col items-center mb-6">
          <img src={logo} alt="Logo" className="w-16 h-16" />
          <h2 className="text-3xl font-bold text-dark mb-2 font-tajawal mt-2">
            التَّحقُّق
          </h2>
          <p className="text-dark text-sm font-tajawal">
            أدخل رقم رمز OTP الخاص بك
          </p>
        </div>
        {/* OTP Inputs */}
        <form className="flex flex-col items-center w-full">
          <div className="flex justify-center gap-2 mb-6 w-full" onPaste={handlePaste}>
            {Array(6).fill(0).map((_, idx) => (
              <input
                key={idx}
                ref={inputs[idx]}
                type="text"
                maxLength={1}
                className="w-12 h-12 text-center text-2xl border-2 border-main2 rounded-lg focus:outline-none focus:border-main1 bg-transparent font-tajawal"
                onChange={e => handleChange(e, idx)}
                inputMode="numeric"
                pattern="[0-9]*"
              />
            ))}
          </div>
          <Button type="submit" className="mb-4 font-tajawal text-xl">
            تأكيد
          </Button>
        </form>
        {/* Resend */}
        <div className="text-center mt-2">
          <span className="text-dark text-base font-tajawal">
            لم أحصل على الرمز؟
          </span>
          <button className="text-main2 font-bold ml-1 font-tajawal">
            إعادة الإرسال
          </button>
        </div>
      </div>
    </div>
  );
}
