import React, { useState } from 'react';
import Loading, { FullPageLoading, CardLoading, ListLoading } from '../Components/Loading';

const LoadingTest = () => {
  const [showFullPage, setShowFullPage] = useState(false);
  const [loadingType, setLoadingType] = useState('spinner');
  const [loadingSize, setLoadingSize] = useState('md');
  const [loadingColor, setLoadingColor] = useState('main1');

  const types = ['spinner', 'dots', 'pulse', 'ring', 'wave'];
  const sizes = ['sm', 'md', 'lg', 'xl'];
  const colors = ['main1', 'main2', 'accent', 'light', 'dark'];

  if (showFullPage) {
    return (
      <div className="relative">
        <button 
          onClick={() => setShowFullPage(false)}
          className="absolute top-4 left-4 z-50 bg-red-500 text-white px-4 py-2 rounded"
        >
          إغلاق
        </button>
        <FullPageLoading text="جاري تحميل الملف ..." />
      </div>
    );
  }

  return (
    <div className="p-4 bg-light min-h-screen direction-rtl">
      <h1 className="text-2xl font-bold text-main1 mb-6">اختبار مكونات التحميل</h1>
      
      {/* التحكم في الإعدادات */}
      <div className="bg-white rounded-lg p-4 mb-6 shadow">
        <h2 className="text-lg font-bold mb-4">إعدادات التحميل</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block text-sm font-medium mb-2">النوع:</label>
            <select 
              value={loadingType} 
              onChange={(e) => setLoadingType(e.target.value)}
              className="w-full p-2 border rounded"
            >
              {types.map(type => (
                <option key={type} value={type}>{type}</option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-sm font-medium mb-2">الحجم:</label>
            <select 
              value={loadingSize} 
              onChange={(e) => setLoadingSize(e.target.value)}
              className="w-full p-2 border rounded"
            >
              {sizes.map(size => (
                <option key={size} value={size}>{size}</option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-sm font-medium mb-2">اللون:</label>
            <select 
              value={loadingColor} 
              onChange={(e) => setLoadingColor(e.target.value)}
              className="w-full p-2 border rounded"
            >
              {colors.map(color => (
                <option key={color} value={color}>{color}</option>
              ))}
            </select>
          </div>
        </div>
      </div>

      {/* عرض مكون Loading الأساسي */}
      <div className="bg-white rounded-lg p-6 mb-6 shadow">
        <h2 className="text-lg font-bold mb-4">مكون Loading الأساسي</h2>
        <div className="flex flex-wrap gap-4">
          <Loading 
            type={loadingType} 
            size={loadingSize} 
            color={loadingColor}
            text="جاري التحميل..."
          />
        </div>
      </div>

      {/* عرض جميع الأنواع */}
      <div className="bg-white rounded-lg p-6 mb-6 shadow">
        <h2 className="text-lg font-bold mb-4">جميع أنواع التحميل</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {types.map(type => (
            <div key={type} className="border rounded p-4">
              <h3 className="font-bold mb-2">{type}</h3>
              <Loading type={type} size="md" color="main1" text={`${type} loading`} />
            </div>
          ))}
        </div>
      </div>

      {/* عرض جميع الأحجام */}
      <div className="bg-white rounded-lg p-6 mb-6 shadow">
        <h2 className="text-lg font-bold mb-4">جميع الأحجام</h2>
        <div className="flex flex-wrap gap-4">
          {sizes.map(size => (
            <div key={size} className="border rounded p-4">
              <h3 className="font-bold mb-2">{size}</h3>
              <Loading type="spinner" size={size} color="main1" />
            </div>
          ))}
        </div>
      </div>

      {/* عرض جميع الألوان */}
      <div className="bg-white rounded-lg p-6 mb-6 shadow">
        <h2 className="text-lg font-bold mb-4">جميع الألوان</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {colors.map(color => (
            <div key={color} className="border rounded p-4">
              <h3 className="font-bold mb-2">{color}</h3>
              <Loading type="spinner" size="md" color={color} />
            </div>
          ))}
        </div>
      </div>

      {/* عرض FullPageLoading */}
      <div className="bg-white rounded-lg p-6 mb-6 shadow">
        <h2 className="text-lg font-bold mb-4">FullPageLoading</h2>
        <button 
          onClick={() => setShowFullPage(true)}
          className="bg-main1 text-white px-4 py-2 rounded hover:bg-main1/90"
        >
          عرض FullPageLoading
        </button>
      </div>

      {/* عرض CardLoading */}
      <div className="bg-white rounded-lg p-6 mb-6 shadow">
        <h2 className="text-lg font-bold mb-4">CardLoading</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <CardLoading />
          <CardLoading />
        </div>
      </div>

      {/* عرض ListLoading */}
      <div className="bg-white rounded-lg p-6 mb-6 shadow">
        <h2 className="text-lg font-bold mb-4">ListLoading</h2>
        <ListLoading count={5} />
      </div>
    </div>
  );
};

export default LoadingTest;
