// src/Components/InputField.jsx
import React, { useState } from "react";
import { FaEye, FaEyeSlash } from "react-icons/fa";

const InputField = ({
  className = "",
  placeholder,
  Icon,
  type = "text",
  name,
  value,
  onChange,
  error
}) => {
  const [showPassword, setShowPassword] = useState(false);
  const isPassword = type === "password";
  const isMobile = name === "mobile";

  const togglePassword = () => setShowPassword(!showPassword);

  // فلترة مباشرة لحقل الموبايل
  const handleInputChange = (e) => {
    let inputValue = e.target.value;

    if (isMobile) {
      // فقط أرقام - طول 10 خانات - يبدأ بـ 09
      inputValue = inputValue.replace(/[^\d]/g, "").slice(0, 10);
    }

    const fakeEvent = {
      ...e,
      target: {
        ...e.target,
        value: inputValue
      }
    };

    onChange(fakeEvent);
  };

  return (
    <div className="w-full">
      <div
        className={`flex items-center bg-grayish rounded-xl px-4 py-2 border ${
          error ? "border-red-500" : "border-transparent"
        } focus-within:border-main1 transition ${className}`}
      >
        <input
          type={isPassword ? (showPassword ? "text" : "password") : type}
          name={name}
          placeholder={placeholder}
          value={value}
          onChange={handleInputChange}
          className="flex-1 bg-transparent outline-none text-right placeholder-gray-500"
          inputMode={isMobile ? "numeric" : undefined}
        />
        {isPassword ? (
          <span
            onClick={togglePassword}
            className="cursor-pointer text-main2 text-lg ml-2"
            title={showPassword ? "إخفاء" : "إظهار"}
          >
            {showPassword ? <FaEyeSlash /> : <FaEye />}
          </span>
        ) : (
          Icon && <Icon className="text-main2 text-lg ml-2" />
        )}
      </div>
      {error && (
        <p className="text-red-500 text-xs mt-1 pr-2 text-right">{error}</p>
      )}
    </div>
  );
};

export default InputField;



