import React from "react";
import { FaHome, FaTachometerAlt, FaBox, FaEnvelope, FaClipboardList, FaCalendarAlt, FaFileAlt, FaComments, FaCog, FaSignOutAlt, FaUserCircle } from "react-icons/fa";
import logo from "../Assets/images/logo.jpg";
import userIcon from "../Assets/images/user.png";
import { useAuth } from '../Context/AuthContext';

const menu = [
  { label: "الرئيسية", icon: <FaHome />, link: "/home" },
  { label: "لوحة التحكم", icon: <FaTachometerAlt />, link: "/dashboard" },
  { label: "المنتجات", icon: <FaBox />, link: "/products" },
  { label: "الرسائل", icon: <FaEnvelope />, link: "/messages" },
  // { label: "الطلبات", icon: <FaClipboardList />, link: "/orders" },
  // { label: "التقويم", icon: <FaCalendarAlt />, link: "/calendar" },
  // { label: "صفحات ثابتة", icon: <FaFileAlt />, link: "/static" },
  // { label: "المستندات", icon: <FaFileAlt />, link: "/documents" },
];

const bottomMenu = [
  { label: "الدردشة", icon: <FaComments />, link: "/chat" },
  { label: "الإعدادات", icon: <FaCog />, link: "/settings" },
  { label: "تسجيل الخروج", icon: <FaSignOutAlt />, link: "/logout" },
];

const Sidebar = () => {
  const { user } = useAuth();
  return (
    <aside className="w-64 min-h-screen bg-main1 border-r border-grayish-200 flex flex-col justify-between font-tajawal">
      <div className="mb-2">
        {/* الشعار واسم الموقع */}
        <div className="flex items-center gap-4 px-2 py-8">
          <img src={logo} alt="شعار الموقع" className="w-10 h-10 rounded-full" />
          <span className="text-xl font-bold text-main2">نمبر ون</span>
        </div>
        {/* قائمة الروابط */}
        <nav className="flex flex-col gap-2 px-4">
          {menu.map((item) => (
            <a
              key={item.label}
              href={item.link}
              className="flex items-center gap-3 px-5 py-2 rounded-lg text-light hover:bg-light hover:text-main1 transition"
            >
              {item.icon}
              <span>{item.label}</span>
            </a>
          ))}
          <hr className="my-4 border-light" />
          {bottomMenu.map((item) => (
            <a 
              key={item.label}
              href={item.link}
              className="flex items-center  gap-3 px-5 py-2 rounded-lg text-light hover:bg-light hover:text-main1 transition"
            >
              {item.icon}
              <span >{item.label}</span>
            </a>
          ))}
        </nav>
      </div>
      {/* معلومات المستخدم */}
      <div className="px-3 py-3 flex items-center gap-3 border-t bg-light shadow-lg rounded-lg m-2" style={{boxShadow: '0 4px 16px 0 rgba(0,0,0,0.10)'}}>
        <span className="flex items-center justify-center w-10 h-10 rounded-full bg-main1">
          <FaUserCircle size={50} color="#fff" />
        </span>
        <div>
          <div className="font-bold text-main1">
            {user ? `ID: ${user.id}` : "غير مسجل"}
          </div>
          <div className="text-xs text-main1">
            {user ? (user.phone_number || user.email || "--------") : "--------"}
          </div>
        </div>
      </div>
    </aside>
  );
};

export default Sidebar;
