// Context/AuthContext.js
import { createContext, useContext, useState, useEffect } from 'react';
import { sessionManager } from '../Config/cookies';
import { logoutUser } from '../API';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true); // ‚úÖ ÿ¨ÿØŸäÿØ

  useEffect(() => {
    const session = sessionManager.restoreSession();

    if (session) {
      console.log('‚úÖ ÿ™ŸÖ ÿßÿ≥ÿ™ÿπÿßÿØÿ© ÿßŸÑÿ¨ŸÑÿ≥ÿ©:', session);
      setUser(session.userData);
      setIsAuthenticated(true);
    } else {
      console.log('‚ùå ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ¨ŸÑÿ≥ÿ© ŸÖÿ≠ŸÅŸàÿ∏ÿ©');
      setUser(null);
      setIsAuthenticated(false);
    }

    setLoading(false); // ‚úÖ ŸÖŸáŸÖ ÿ¨ÿØŸãÿß
  }, []);

  useEffect(() => {
    const handleSessionExpired = () => {
      console.log('üîÑ ÿßŸÜÿ™Ÿáÿßÿ° ÿßŸÑÿ¨ŸÑÿ≥ÿ©');
      sessionManager.clearSession();
      setUser(null);
      setIsAuthenticated(false);
      window.location.href = '/login';
    };

    window.addEventListener('sessionExpired', handleSessionExpired);
    return () => {
      window.removeEventListener('sessionExpired', handleSessionExpired);
    };
  }, []);

  const login = (response) => {
    sessionManager.saveSession(response);
    const userInfo = {
      id: response.user.id,
      first_name: response.user.first_name,
      last_name: response.user.last_name,
      is_active: response.user.is_active,
      role: response.user.role
    };
    setUser(userInfo);
    setIsAuthenticated(true);
  };

  const logout = async () => {
    try {
      await logoutUser();
    } catch (error) {
      console.warn('ŸÅÿ¥ŸÑ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨:', error.message);
    }

    sessionManager.clearSession();
    setUser(null);
    setIsAuthenticated(false);
    window.location.href = '/login';
  };

  const clearSession = async () => {
    try {
      await logoutUser();
    } catch {}
    sessionManager.clearSession();
    setUser(null);
    setIsAuthenticated(false);
    window.location.href = '/login';
  };

  const getAccessToken = () => {
    return sessionManager.restoreSession()?.accessToken;
  };

  const getRefreshToken = () => {
    return sessionManager.restoreSession()?.refreshToken;
  };

  const value = {
    user,
    isAuthenticated,
    loading, // ‚úÖ ÿ•ÿ∂ÿßŸÅÿ© Ÿáÿ∞Ÿá ÿßŸÑŸÇŸäŸÖÿ©
    login,
    logout,
    clearSession,
    getAccessToken,
    getRefreshToken
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};




