import React, { useState } from "react";
import {  FaLock, FaMobileAlt } from "react-icons/fa";
import logo from "../../Assets/images/logo.jpg";
import InputField from "../../Components/InputField";
import Button from "../../Components/Button";
import { Link } from "react-router-dom";
import {  handlePhoneInputKeyPress } from '../../Utils/validateForm';
// import { loginUser } from '../../API/Auth'; // فعل هذا إذا كان لديك دالة تسجيل دخول

const Login = () => {
  const [phone, setPhone] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  // const navigate = useNavigate(); // إذا كنت تريد الانتقال بعد النجاح

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // await loginUser({ phone_number: phone, password });
      setError("");
      // navigate("/home");
    } catch (error) {
      let msg = "فشل تسجيل الدخول. تحقق من البيانات أو الشبكة.";
      if (error.response && error.response.data) {
        if (typeof error.response.data === "string") {
          msg = error.response.data;
        } else if (typeof error.response.data === "object") {
          msg = Object.values(error.response.data).flat().join("، ");
        }
      }
      setError(msg);
    }
  };

  return (
    <div className="min-h-screen bg-light flex items-center justify-center  direction-rtl">
      <div className="w-full max-w-lg p-6 bg-light rounded-2xl shadow-md text-dark">
        <div  className="flex flex-col items-center mb-6">
          <img src={logo} alt="Logo" className="w-16 h-16" />
          <h3 className="text-2xl font-bold mt-2  text-main1">اهلا بعودتك </h3>
          <p className="text-sm text-grayish-600"> سجل الدخول للوصول الى حسابك </p>
        </div>

<form className="space-y-4" onSubmit={handleSubmit}>
    <div className="space-y-4">
        <InputField className="p-3 mb-2" placeholder="رقم الموبايل" Icon={FaMobileAlt} type="text"
          value={phone}
          onChange={e => setPhone(e.target.value)}
          onKeyPress={e => handlePhoneInputKeyPress(e, e.target.value)}
        />
        <InputField className="p-3 mb-2" placeholder="كلمة السر" Icon={FaLock} type="password"
          value={password}
          onChange={e => setPassword(e.target.value)}
        />
    </div>
    {error && (
      <div className="w-full flex items-center gap-2 bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-lg mt-2 text-sm font-tajawal animate-fade-in" role="alert">
        <svg className="w-5 h-5 flex-shrink-0" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" d="M12 9v2m0 4h.01m-6.938 4h13.856c1.054 0 1.918-.816 1.995-1.85l.007-.15V6c0-1.054-.816-1.918-1.85-1.995L19 4H5c-1.054 0-1.918.816-1.995 1.85L3 6v12c0 1.054.816 1.918 1.85 1.995L5 20z" />
        </svg>
        <span>{error}</span>
      </div>
    )}
    <div className="text-right mt-4 text-sm">
          <Link to="/forgetPassword" className="text-main2 font-bold">
            هل نسيت كلمة المرور؟
          </Link>
        </div>
        <Button type="submit">تسجيل دخول</Button>
</form>
      </div>
    </div>
  );
};

export default Login;
