import React from "react";

const InputField = ({ placeholder, Icon, type = "text", value, onChange }) => (
  <div className="flex flex-row-reverse items-center bg-grayish rounded-xl px-4 py-2 w-full border border-transparent focus-within:border-main2 transition">
    <input
      type={type}
      placeholder={placeholder}
      value={value}
      onChange={onChange}
      className="flex-1 bg-transparent outline-none text-right placeholder-gray-500"
    />
    {Icon && <Icon className="text-main2 text-lg ml-2" />}
  </div>
);

export default InputField;
