import React from 'react';
import { FaCamera, FaEdit, FaUser, FaUserFriends, FaUserTag, FaRegCalendarAlt, FaPhone, FaCrown, FaCalendarPlus, FaCheckCircle, FaSpinner } from 'react-icons/fa';
import { useAuth } from '../../Context/AuthContext';
import useProfile from '../../Hooks/useProfile';
import Alert from '../../Components/Alert';
import userImg from '../../Assets/images/user.png';

const Profile = () => {
  const { user } = useAuth();
  const { profile, loading, error, refreshProfile } = useProfile();

  // ÿØÿßŸÑÿ© ŸÑŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑŸÇŸäŸÖ ÿßŸÑŸÅÿßÿ±ÿ∫ÿ©
  const formatValue = (value, showPlaceholder = true) => {
    if (value === null || value === undefined || value === '') {
      return showPlaceholder ? 'ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ' : '';
    }
    return value;
  };

  // ÿØÿßŸÑÿ© ŸÑÿ™ÿ≠ŸàŸäŸÑ ÿßŸÑÿØŸàÿ± ÿ•ŸÑŸâ ÿßŸÑÿπÿ±ÿ®Ÿäÿ©
  const translateRole = (role) => {
    const roleTranslations = {
      'super_admin': 'ŸÖÿØŸäÿ± ÿπÿßŸÖ',
      'admin': 'ŸÖÿØŸäÿ±',
      'user': 'ŸÖÿ≥ÿ™ÿÆÿØŸÖ',
      'merchant': 'ÿ™ÿßÿ¨ÿ±',
      'customer': 'ÿπŸÖŸäŸÑ',
      'staff': 'ŸÖŸàÿ∏ŸÅ',
      'moderator': 'ŸÖÿ¥ÿ±ŸÅ'
    };
    
    return roleTranslations[role] || role || 'ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ';
  };

  // ÿØÿßŸÑÿ© ŸÑÿ™ŸÜÿ≥ŸäŸÇ ÿßŸÑÿ™ÿßÿ±ŸäÿÆ
  const formatDate = (dateString) => {
    if (!dateString) return 'ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ';
    
    try {
      const date = new Date(dateString);
