import React from "react";
import { FaUser, FaLock, FaMobileAlt } from "react-icons/fa";


const InputField = ({ placeholder, Icon, type = "text" }) => (
  <div className="flex items-center bg-grayish rounded-xl px-4 py-2">
    <Icon className="text-main2 text-lg mr-2" />
    <input
      type={type}
      placeholder={placeholder}
      className="flex-1 bg-transparent outline-none text-right placeholder-gray-500"
    />
  </div>
);


const Register = () => {
  return (
    <div className="min-h-screen bg-light flex items-center justify-center font-tajawal direction-rtl">
      <div className="w-full max-w-md p-6 bg-light rounded-2xl shadow-md text-dark">
        <div className="flex flex-col items-center mb-6">
          <img src="../../Assets/" alt="Logo" className="w-16 h-16" />
          <h2 className="text-2xl font-bold mt-2">لنبدأ</h2>
          <p className="text-sm text-gray-600">بإنشاء حساب مجاني</p>
        </div>

<form className="space-y-4">
  <InputField placeholder="الاسم الأول" Icon={FaUser} />
  <InputField placeholder="اسم الأب" Icon={FaUser} />
  <InputField placeholder="الكنية" Icon={FaUser} />
  <InputField placeholder="رقم الموبايل" Icon={FaMobileAlt} />
  <InputField placeholder="كلمة السر" Icon={FaLock} type="password" />
  <InputField placeholder="تأكيد كلمة السر" Icon={FaLock} type="password" />

  <button
    type="submit"
    className="w-full bg-main1 text-white py-2 rounded-xl text-lg hover:bg-opacity-90 transition"
  >
    التالي
  </button>
</form>

        <div className="text-center mt-4 text-sm">
          <span>تمتلك حساب بالفعل؟ </span>
          <button  className="text-main2 font-bold">سجل دخول</button>
        </div>
      </div>
    </div>
  );
};

export default Register;
