import axios from 'axios';


const axiosInstance = axios.create({
  baseURL: 'http://127.0.0.1:8000/api/', // عدل هذا العنوان حسب بيئة العمل
  headers: {
    'Content-Type': 'application/json',
    // أضف أي هيدر آخر تحتاجه هنا
  },
  // يمكنك إضافة إعدادات أخرى مثل timeout, withCredentials, ...
});

export default axiosInstance;
