import axios from 'axios';
import { tokenManager } from './cookies';
import {
  baseURL
} from '../API/Api';

const axiosInstance = axios.create({
  baseURL: baseURL, // عدل هذا العنوان حسب بيئة العمل
  headers: {
    'Content-Type': 'application/json',
    // أضف أي هيدر آخر تحتاجه هنا
  },
  // يمكنك إضافة إعدادات أخرى مثل timeout, withCredentials, ...
});

// إضافة التوكن تلقائياً لكل طلب
axiosInstance.interceptors.request.use(
  (config) => {
    const token = tokenManager.getAccessToken();
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// معالجة استجابة الطلبات
axiosInstance.interceptors.response.use(
  (response) => {
    return response;
  },
  async (error) => {
    const originalRequest = error.config;

    // إذا كان الخطأ 401 ولم نكن قد حاولنا تحديث التوكن من قبل
    if (error.response?.status === 401 && !originalRequest._retry) {
      originalRequest._retry = true;

      try {
        const refreshToken = tokenManager.getRefreshToken();
        if (refreshToken) {
          // يمكنك إضافة طلب لتحديث التوكن هنا
          // const response = await refreshTokenAPI(refreshToken);
          // tokenManager.updateTokens(response.data.access, response.data.refresh);
          // originalRequest.headers.Authorization = `Bearer ${response.data.access}`;
          // return axiosInstance(originalRequest);
        }
      } catch (refreshError) {
        // إذا فشل تحديث التوكن، احذف الجلسة وانتقل لصفحة تسجيل الدخول
        const { sessionManager } = require('./cookies');
        sessionManager.clearSession();
        window.location.href = '/login';
      }
    }

    return Promise.reject(error);
  }
);

export default axiosInstance;
