import axios from 'axios';
import { sessionManager } from './cookies';
import { handleUnauthorizedError } from '../Utils/errorHandler';
import {
  baseURL
} from '../API/Api';

// axios ŸÑŸÑÿ∑ŸÑÿ®ÿßÿ™ ÿßŸÑÿπÿßŸÖÿ© (ÿ®ÿØŸàŸÜ ÿ™ŸàŸÉŸÜ)
export const publicAxios = axios.create({
  baseURL: baseURL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// axios ŸÑŸÑÿ∑ŸÑÿ®ÿßÿ™ ÿßŸÑŸÖÿ≠ŸÖŸäÿ© (ŸÖÿπ ÿ™ŸàŸÉŸÜ)
const axiosInstance = axios.create({
  baseURL: baseURL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑÿ™ŸàŸÉŸÜ ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã ŸÑŸÉŸÑ ÿ∑ŸÑÿ®
axiosInstance.interceptors.request.use(
  (config) => {
    const session = sessionManager.restoreSession();
    const token = session?.accessToken;
    
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
      console.log('üîë ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑÿ™ŸàŸÉŸÜ ŸÑŸÑÿ∑ŸÑÿ®:', config.url);
    } else {
      console.log('‚ö†Ô∏è ŸÑÿß ŸäŸàÿ¨ÿØ ÿ™ŸàŸÉŸÜ ŸÑŸÑÿ∑ŸÑÿ®:', config.url);
      console.log('üîç ÿ≠ÿßŸÑÿ© ÿßŸÑÿ¨ŸÑÿ≥ÿ©:', sessionManager.hasSession());
    }
    return config;
  },
  (error) => {
    console.log('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ÿπÿØÿßÿØ ÿßŸÑÿ∑ŸÑÿ®:', error);
    return Promise.reject(error);
  }
);

// ŸÖÿπÿßŸÑÿ¨ÿ© ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™
axiosInstance.interceptors.response.use(
  (response) => {
    console.log('‚úÖ ŸÜÿ¨ÿ≠ ÿßŸÑÿ∑ŸÑÿ®:', response.config.url);
    return response;
  },
  async (error) => {
    console.log('‚ùå ŸÅÿ¥ŸÑ ÿßŸÑÿ∑ŸÑÿ®:', error.config?.url, error.response?.status);
    
    // ŸÖÿπÿßŸÑÿ¨ÿ© ÿÆÿ∑ÿ£ 401 (ÿ∫Ÿäÿ± ŸÖÿµÿ±ÿ≠)
    if (error.response?.status === 401) {
      return handleUnauthorizedError(error, error.config, axiosInstance);
    }
    
    return Promise.reject(error);
  }
);

export default axiosInstance;
