import React from "react";

const Alert = ({ children, type = "error", className = "" }) => {
  const color =
    type === "success"
      ? "bg-green-100 border border-green-400 text-green-700"
      : "bg-red-100 border border-red-400 text-red-700";
  const icon = type === "success"
    ? (
      <svg className="w-5 h-5 flex-shrink-0" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24">
        <path strokeLinecap="round" strokeLinejoin="round" d="M5 13l4 4L19 7" />
      </svg>
    )
    : (
      <svg className="w-5 h-5 flex-shrink-0" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24">
        <path strokeLinecap="round" strokeLinejoin="round" d="M12 9v2m0 4h.01m-6.938 4h13.856c1.054 0 1.918-.816 1.995-1.85l.007-.15V6c0-1.054-.816-1.918-1.85-1.995L19 4H5c-1.054 0-1.918.816-1.995 1.85L3 6v12c0 1.054.816 1.918 1.85 1.995L5 20z" />
      </svg>
    );
  return (
    <div
      className={`w-full flex items-center gap-5 ${color} px-4 py-3 rounded-lg mt-3 text-sm font-tajawal animate-fade-in ${className}`}
      role="alert"
    >
      {icon}
      <span>{children}</span>
    </div>
  );
};

export default Alert;
