import React, { useState } from "react";
import { FaHome, FaTachometerAlt, FaBox, FaEnvelope, FaClipboardList, FaCalendarAlt, FaFileAlt, FaComments, FaCog, FaUserCircle, FaTimes, FaBars } from "react-icons/fa";
import { Link } from "react-router-dom";
import logo from "../Assets/images/logo.jpg";
import { useAuth } from '../Context/AuthContext';
import LogoutButton from './LogoutButton';
import LogoutErrorAlert from './LogoutErrorAlert';
import { formatUserName } from '../Utils/userUtils';

const menu = [
  { label: "الرئيسية", icon: <FaHome />, link: "/home" },
  { label: "لوحة التحكم", icon: <FaTachometerAlt />, link: "/dashboard" },
  { label: "المنتجات", icon: <FaBox />, link: "/products" },
  { label: "الرسائل", icon: <FaEnvelope />, link: "/messages" },
  // { label: "الطلبات", icon: <FaClipboardList />, link: "/orders" },
  // { label: "التقويم", icon: <FaCalendarAlt />, link: "/calendar" },
  // { label: "صفحات ثابتة", icon: <FaFileAlt />, link: "/static" },
  // { label: "المستندات", icon: <FaFileAlt />, link: "/documents" },
];

const bottomMenu = [
  { label: "الدردشة", icon: <FaComments />, link: "/chat" },
  { label: "الإعدادات", icon: <FaCog />, link: "/settings" },
];

const Sidebar = () => {
  // حالة لإظهار أو إخفاء الشريط الجانبي في الشاشات الصغيرة
  const [isOpen, setIsOpen] = useState(false);
  const { user } = useAuth();

  // دالة لفتح الشريط الجانبي
  const openSidebar = () => setIsOpen(true);
  // دالة لإغلاق الشريط الجانبي
  const closeSidebar = () => setIsOpen(false);

  return (
    <>
      {/* زر الهامبرغر - يظهر فقط في الشاشات الصغيرة */}
      <button
        className="md:hidden fixed top-4 right-4 z-50 bg-main1 text-light p-2 rounded-lg shadow-lg focus:outline-none"
        onClick={openSidebar}
        aria-label="فتح القائمة الجانبية"
      >
        <FaBars size={24} />
      </button>

      {/* طبقة خلفية عند فتح الشريط الجانبي في الجوال */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black bg-opacity-40 z-40 md:hidden"
          onClick={closeSidebar}
        ></div>
      )}

      {/* الشريط الجانبي */}
      <aside
        className={`
          fixed top-0 right-0 z-50
          w-64 min-h-screen bg-main1 border-grayish flex flex-col justify-between font-tajawal
          transition-transform duration-300
          md:static md:translate-x-0 md:flex
          ${isOpen ? 'translate-x-0' : 'translate-x-full'}
          md:!block
        `}
        style={{ boxShadow: '0 4px 16px 0 rgba(0,0,0,0.10)' }}
      >
        <div className="mb-2">
          {/* زر إغلاق - يظهر فقط في الجوال */}
          <div className="flex justify-end md:hidden p-2">
            <button
              onClick={closeSidebar}
              className="text-main2 bg-light rounded-full p-1 hover:bg-grayish focus:outline-none"
              aria-label="إغلاق القائمة الجانبية"
            >
              <FaTimes size={22} />
            </button>
          </div>

          {/* الشعار واسم الموقع */}
          <div className="flex items-center gap-4 px-2 py-8">
            <img src={logo} alt="شعار الموقع" className="w-10 h-10 rounded-full" />
            <span className="text-xl font-bold text-main2">نمبر ون</span>
          </div>

          {/* رسالة خطأ تسجيل الخروج */}
          <LogoutErrorAlert />

          {/* قائمة الروابط */}
          <nav className="flex flex-col gap-2 px-4">
            {menu.map((item) => (
              <Link
                key={item.label}
                to={item.link}
                className="flex items-center gap-3 px-5 py-2 rounded-lg text-light hover:bg-light hover:text-main1 transition"
                onClick={closeSidebar} // إغلاق القائمة عند الضغط في الجوال
              >
                {item.icon}
                <span>{item.label}</span>
              </Link>
            ))}
            <hr className="my-4 border-light" />
            {bottomMenu.map((item) => (
              <Link
                key={item.label}
                to={item.link}
                className="flex items-center  gap-3 px-5 py-2 rounded-lg text-light hover:bg-light hover:text-main1 transition"
                onClick={closeSidebar} // إغلاق القائمة عند الضغط في الجوال
              >
                {item.icon}
                <span >{item.label}</span>
              </Link>
            ))}
            {/* زر تسجيل الخروج */}
            <LogoutButton />
          </nav>
        </div>
        {/* معلومات المستخدم */}
        <Link
          to="/profile"
          className="px-3 py-3 flex items-center gap-3 border-t bg-light shadow-lg rounded-lg m-2 hover:bg-grayish transition cursor-pointer"
          style={{ boxShadow: '0 4px 16px 0 rgba(0,0,0,0.10)' }}
          onClick={closeSidebar} // إغلاق القائمة عند الضغط في الجوال
        >
          <span className="flex items-center justify-center w-10 h-10 rounded-full bg-main1">
            <FaUserCircle size={32} color="#FFF9ED" />
          </span>
          <div>
            <div className="font-bold text-main1">
              {formatUserName(user)}
            </div>
            <div className="text-xs text-gray-500 mt-1">
              انقر لعرض الملف الشخصي
            </div>
          </div>
        </Link>
      </aside>
    </>
  );
};

export default Sidebar;
