import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { loginUser } from '../API/Auth';
import { validateLoginForm, getInstantPhoneError } from '../Utils/validateForm';
import { useAuth } from '../Context/AuthContext';

export default function useLoginForm() {
  const [values, setValues] = useState({
    phone_number: '',
    password: ''
  });
  const [errors, setErrors] = useState({});
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();
  const { login, logout } = useAuth();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setValues((prev) => ({ ...prev, [name]: value }));
    // تحقق فوري من صحة رقم الهاتف
    if (name === 'phone_number') {
      const phoneError = getInstantPhoneError(value);
      if (phoneError) {
        setErrors((prev) => ({ ...prev, phone_number: phoneError }));
      } else {
        setErrors((prev) => {
          const { phone_number, ...rest } = prev;
          return rest;
        });
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setIsLoading(true);
    
    const validationErrors = validateLoginForm(values);
    if (Object.keys(validationErrors).length === 0) {
      try {
        const res = await loginUser({
          phone_number: values.phone_number,
          password: values.password
        });
        
        console.log('Login response:', res);
        
        // التحقق من حالة الحساب
        if (!res.user.is_active) {
          setError("حسابك غير مفعل. يرجى التواصل مع الإدارة لتفعيل الحساب.");
          return;
        }
        
        // تخزين البيانات في Context والكوكيز
        login(res);
        
        // التوجيه حسب دور المستخدم
        if (res.user.is_superuser) {
          navigate("/super-admin");
        } else if (res.user.role === "merchant") {
          navigate("/merchant");
        } else {
          navigate("/home");
        }
        
      } catch (error) {
        let msg = "فشل تسجيل الدخول. تحقق من البيانات أو الشبكة.";
        if (error.response && error.response.data) {
          if (typeof error.response.data === "string") {
            msg = error.response.data;
          } else if (typeof error.response.data === "object") {
            msg = Object.values(error.response.data).flat().join("، ");
          }
        }
        setError(msg);
      }
    } else {
      setErrors(validationErrors);
    }
    
    setIsLoading(false);
  };

  return {
    values,
    errors,
    error,
    isLoading,
    handleChange,
    handleSubmit
  };
}
