import React, { useState } from 'react';
import { FaUser, FaUserFriends, FaUserTag, FaPhone, FaSave, FaTimes } from 'react-icons/fa';
import { formatValue } from '../../Utils';

const UpdateProfile = ({ profile, onClose, onUpdate }) => {
  const [formData, setFormData] = useState({
    first_name: profile?.first_name || '',
    father_name: profile?.father_name || '',
    last_name: profile?.last_name || '',
    phone_number: profile?.phone_number || ''
  });

  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    try {
      // ŸáŸÜÿß ÿ≥Ÿäÿ™ŸÖ ÿ•ÿ∂ÿßŸÅÿ© API call ŸÑÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
      console.log('üìù ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ™ÿ≠ÿØŸäÿ´:', formData);
      
      // ŸÖÿ≠ÿßŸÉÿßÿ© API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      // ÿßÿ≥ÿ™ÿØÿπÿßÿ° ÿØÿßŸÑÿ© ÿßŸÑÿ™ÿ≠ÿØŸäÿ´
      if (onUpdate) {
        onUpdate(formData);
      }
      
      onClose();
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑŸÖŸÑŸÅ ÿßŸÑÿ¥ÿÆÿµŸä:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="direction-rtl">
      <form onSubmit={handleSubmit} className="space-y-4">
        {/* ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑÿ£ŸàŸÑ */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2 font-tajawal">
            <FaUser className="inline mr-2 text-main1" />
            ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑÿ£ŸàŸÑ
          </label>
          <input
            type="text"
            name="first_name"
            value={formData.first_name}
            onChange={handleChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-main1 focus:border-transparent font-tajawal"
            placeholder="ÿ£ÿØÿÆŸÑ ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑÿ£ŸàŸÑ"
            required
          />
        </div>

        {/* ÿßÿ≥ŸÖ ÿßŸÑÿ£ÿ® */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2 font-tajawal">
            <FaUserFriends className="inline mr-2 text-main1" />
            ÿßÿ≥ŸÖ ÿßŸÑÿ£ÿ®
          </label>
          <input
            type="text"
            name="father_name"
            value={formData.father_name}
            onChange={handleChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-main1 focus:border-transparent font-tajawal"
            placeholder="ÿ£ÿØÿÆŸÑ ÿßÿ≥ŸÖ ÿßŸÑÿ£ÿ®"
          />
        </div>

        {/* ÿßŸÑŸÉŸÜŸäÿ© */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2 font-tajawal">
            <FaUserTag className="inline mr-2 text-main1" />
            ÿßŸÑŸÉŸÜŸäÿ©
          </label>
          <input
            type="text"
            name="last_name"
            value={formData.last_name}
            onChange={handleChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-main1 focus:border-transparent font-tajawal"
            placeholder="ÿ£ÿØÿÆŸÑ ÿßŸÑŸÉŸÜŸäÿ©"
            required
          />
        </div>

        {/* ÿ±ŸÇŸÖ ÿßŸÑŸÖŸàÿ®ÿßŸäŸÑ */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2 font-tajawal">
            <FaPhone className="inline mr-2 text-main1" />
            ÿ±ŸÇŸÖ ÿßŸÑŸÖŸàÿ®ÿßŸäŸÑ
          </label>
          <input
            type="tel"
            name="phone_number"
            value={formData.phone_number}
            onChange={handleChange}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-main1 focus:border-transparent font-tajawal"
            placeholder="ÿ£ÿØÿÆŸÑ ÿ±ŸÇŸÖ ÿßŸÑŸÖŸàÿ®ÿßŸäŸÑ"
            required
          />
        </div>

        {/* ÿ£ÿ≤ÿ±ÿßÿ± ÿßŸÑÿ™ÿ≠ŸÉŸÖ */}
        <div className="flex gap-3 pt-4">
          <button
            type="submit"
            disabled={loading}
            className="flex-1 flex items-center justify-center gap-2 bg-main1 text-white px-4 py-2 rounded-lg hover:bg-main1/90 transition disabled:opacity-50 font-tajawal"
          >
            {loading ? (
              <>
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ≠ŸÅÿ∏...
              </>
            ) : (
              <>
                <FaSave size={16} />
                ÿ≠ŸÅÿ∏ ÿßŸÑÿ™ÿ∫ŸäŸäÿ±ÿßÿ™
              </>
            )}
          </button>
          
          <button
            type="button"
            onClick={onClose}
            className="flex-1 flex items-center justify-center gap-2 bg-gray-300 text-gray-700 px-4 py-2 rounded-lg hover:bg-gray-400 transition font-tajawal"
          >
            <FaTimes size={16} />
            ÿ•ŸÑÿ∫ÿßÿ°
          </button>
        </div>
      </form>
    </div>
  );
};

export default UpdateProfile;
