import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import logo from "../../Assets/images/logo.jpg";
import InputField from "../../Components/InputField";
import Button from "../../Components/Button";
import Alert from "../../Components/Alert";
import { FaLock } from "react-icons/fa";
import { resetPassword } from "../../API/Auth";

const ResetPassword = () => {
  const { state } = useLocation();
  const navigate = useNavigate();
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    if (!password || !confirmPassword) {
      setError("يرجى إدخال كلمة المرور وتأكيدها.");
      return;
    }
    if (password !== confirmPassword) {
      setError("كلمتا المرور غير متطابقتين.");
      return;
    }
    setLoading(true);
    try {
      await resetPassword({
        phone_number: state?.phone_number,
        otp_code: state?.otp_code,
        password,
        confirm_password: confirmPassword
      });
      setSuccess("تم تعيين كلمة المرور بنجاح! سيتم تحويلك لتسجيل الدخول...");
      setTimeout(() => navigate("/login"), 2000);
    } catch (err) {
      let msg = "فشل تعيين كلمة المرور. حاول مرة أخرى.";
      if (err.response && err.response.data) {
        if (typeof err.response.data === "string") {
          msg = err.response.data;
        } else if (typeof err.response.data === "object") {
          msg = Object.values(err.response.data).flat().join("، ");
        }
      }
      setError(msg);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-light direction-rtl">
      <div className="bg-light rounded-3xl shadow-lg p-8 pt-4 w-full max-w-md flex flex-col items-center">
        <img src={logo} alt="Logo" className="w-20 h-20 mb-2" />
        <h2 className="text-2xl font-bold text-main1 mb-1 font-tajawal">إعادة تعيين كلمة المرور</h2>
        <p className="text-dark text-sm mb-6 font-tajawal">أدخل كلمة المرور الجديدة</p>
        <form className="w-full flex flex-col gap-4" onSubmit={handleSubmit}>
          <InputField
            type="password"
            placeholder="كلمة السر"
            Icon={FaLock}
            value={password}
            onChange={e => setPassword(e.target.value)}
            className="font-tajawal"
          />
          <InputField
            type="password"
            placeholder="تأكيد كلمة السر"
            Icon={FaLock}
            value={confirmPassword}
            onChange={e => setConfirmPassword(e.target.value)}
            className="font-tajawal"
          />
          {error && <Alert>{error}</Alert>}
          {success && <div className="bg-green-100 text-green-800 p-2 rounded text-center font-tajawal">{success}</div>}
          <Button type="submit" className="mt-2 font-tajawal text-xl" disabled={loading}>
            {loading ? "...جاري الإرسال" : <><span>إرسال</span> <span className="ml-2">📤</span></>}
          </Button>
        </form>
      </div>
    </div>
  );
};

export default ResetPassword;
