import React, { useState, useEffect } from 'react';
import { ListLoading, CardLoading } from '../../Components/Loading';

const UsersList = () => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);

    // محاكاة جلب البيانات
    useEffect(() => {
        const fetchUsers = async () => {
            try {
                // محاكاة طلب API
                await new Promise(resolve => setTimeout(resolve, 2000));
                
                // بيانات وهمية للمستخدمين
                const mockUsers = [
                    { id: 1, name: "أحمد محمد", email: "ahmed@example.com", role: "مدير" },
                    { id: 2, name: "فاطمة علي", email: "fatima@example.com", role: "مستخدم" },
                    { id: 3, name: "محمد أحمد", email: "mohamed@example.com", role: "مشرف" },
                ];
                
                setUsers(mockUsers);
            } catch (error) {
                console.error('خطأ في جلب البيانات:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchUsers();
    }, []);

    if (loading) {
        return (
            <div className="p-6">
                <h2 className="text-2xl font-bold text-main1 mb-6">قائمة المستخدمين</h2>
                <ListLoading count={5} />
            </div>
        );
    }

    return (
        <div className="p-6">
            <h2 className="text-2xl font-bold text-main1 mb-6">قائمة المستخدمين</h2>
            
            {users.length === 0 ? (
                <div className="text-center py-8">
                    <p className="text-gray-500">لا يوجد مستخدمين</p>
                </div>
            ) : (
                <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
                    {users.map(user => (
                        <div key={user.id} className="bg-white rounded-xl p-6 shadow-sm border border-gray-100">
                            <div className="flex items-center space-x-3 rtl:space-x-reverse mb-4">
                                <div className="w-12 h-12 bg-main1 rounded-full flex items-center justify-center">
                                    <span className="text-light font-bold text-lg">
                                        {user.name.charAt(0)}
                                    </span>
                                </div>
                                <div>
                                    <h3 className="font-bold text-main1">{user.name}</h3>
                                    <p className="text-sm text-gray-600">{user.email}</p>
                                </div>
                            </div>
                            <div className="flex justify-between items-center">
                                <span className="px-3 py-1 bg-main2 text-light rounded-full text-sm">
                                    {user.role}
                                </span>
                                <button className="text-main1 hover:text-main2 transition-colors">
                                    عرض التفاصيل
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default UsersList;
