import React from "react";

const colorMap = {
  error: {
    bg: "bg-red-100",
    border: "border-red-400",
    text: "text-red-700",
    icon: (
      <svg className="w-5 h-5 flex-shrink-0" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24">
        <path strokeLinecap="round" strokeLinejoin="round" d="M12 9v2m0 4h.01m-6.938 4h13.856c1.054 0 1.918-.816 1.995-1.85l.007-.15V6c0-1.054-.816-1.918-1.85-1.995L19 4H5c-1.054 0-1.918.816-1.995 1.85L3 6v12c0 1.054.816 1.918 1.85 1.995L5 20z" />
      </svg>
    )
  },
  success: {
    bg: "bg-green-100",
    border: "border-green-400",
    text: "text-green-700",
    icon: (
      <svg className="w-5 h-5 flex-shrink-0" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24">
        <path strokeLinecap="round" strokeLinejoin="round" d="M5 13l4 4L19 7" />
      </svg>
    )
  },
  warning: {
    bg: "bg-yellow-100",
    border: "border-yellow-400",
    text: "text-yellow-700",
    icon: (
      <svg className="w-5 h-5 flex-shrink-0" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24">
        <path strokeLinecap="round" strokeLinejoin="round" d="M12 8v4m0 4h.01M4.93 19.07a10 10 0 1114.14 0A10 10 0 014.93 19.07z" />
      </svg>
    )
  },
  info: {
    bg: "bg-blue-100",
    border: "border-blue-400",
    text: "text-blue-700",
    icon: (
      <svg className="w-5 h-5 flex-shrink-0" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24">
        <path strokeLinecap="round" strokeLinejoin="round" d="M13 16h-1v-4h-1m1-4h.01" />
      </svg>
    )
  }
};

const Alert = ({ children, type = "error", className = "", onClose }) => {
  const color = colorMap[type] || colorMap.error;
  return (
    <div
      className={`w-full flex items-center gap-2 ${color.bg} ${color.border} ${color.text} px-4 py-3 rounded-lg mt-2 text-sm font-tajawal animate-fade-in ${className}`}
      role="alert"
    >
      {color.icon}
      <span className="flex-1">{children}</span>
      {onClose && (
        <button onClick={onClose} className="ml-2 text-lg font-bold focus:outline-none" aria-label="إغلاق التنبيه">&times;</button>
      )}
    </div>
  );
};

export default Alert;
