import axios from 'axios';
import { tokenManager } from './cookies';
import {
  baseURL
} from '../API/Api';

// axios ŸÑŸÑÿ∑ŸÑÿ®ÿßÿ™ ÿßŸÑÿπÿßŸÖÿ© (ÿ®ÿØŸàŸÜ ÿ™ŸàŸÉŸÜ)
export const publicAxios = axios.create({
  baseURL: baseURL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// axios ŸÑŸÑÿ∑ŸÑÿ®ÿßÿ™ ÿßŸÑŸÖÿ≠ŸÖŸäÿ© (ŸÖÿπ ÿ™ŸàŸÉŸÜ)
const axiosInstance = axios.create({
  baseURL: baseURL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑÿ™ŸàŸÉŸÜ ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã ŸÑŸÉŸÑ ÿ∑ŸÑÿ®
axiosInstance.interceptors.request.use(
  (config) => {
    const token = tokenManager.getAccessToken();
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
      console.log('üîë ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑÿ™ŸàŸÉŸÜ ŸÑŸÑÿ∑ŸÑÿ®:', config.url);
    } else {
      console.log('‚ö†Ô∏è ŸÑÿß ŸäŸàÿ¨ÿØ ÿ™ŸàŸÉŸÜ ŸÑŸÑÿ∑ŸÑÿ®:', config.url);
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// ŸÖÿπÿßŸÑÿ¨ÿ© ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™ - ŸÅŸÇÿ∑ ŸÑŸÑŸÄ logging
axiosInstance.interceptors.response.use(
  (response) => {
    console.log('‚úÖ ŸÜÿ¨ÿ≠ ÿßŸÑÿ∑ŸÑÿ®:', response.config.url);
    return response;
  },
  (error) => {
    console.log('‚ùå ŸÅÿ¥ŸÑ ÿßŸÑÿ∑ŸÑÿ®:', error.config?.url, error.response?.status);
    return Promise.reject(error);
  }
);

export default axiosInstance;
